    id = json['id'];
    name = json['name'];
    originalName = json['original_name'];
    popularity = json['popularity'];
    profilePath = json['profile_path'];
    castId = json['cast_id'];
    character = json['character'];
    creditId = json['credit_id'];
    order = json['order'];
    job = json['job'];
  }

  getPhoto() {
    if (profilePath == null) {
      return 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fcrowd-literature.eu%2Fspeakers%2Fno-avatar%2F&psig=AOvVaw2CUztJhuRaIYR23aVV3_Hh&ust=1612610042485000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCLDcgdzO0u4CFQAAAAAdAAAAABAD';
