class Cast {
  List<Actor> actores = new List();

  Cast.fromJsonList(List<dynamic> jsonList) {
    if (jsonList == null) {
      return;
    }

    jsonList.forEach((act) {
      final actor = Actor.fromJsonMap(act);
      actores.add(actor);
    });
  }
}
